// Declares a dictionary's functionality

#ifndef DICTIONARY_H
#define DICTIONARY_H

#include <stdbool.h>

// Maximum length for a word
// (e.g., pneumonoultramicroscopicsilicovolcanoconiosis)
#define LENGTH 45

// Prototypes
bool check(const char *word);
bool load(const char *dictionary);
unsigned int size(void);
bool unload(void);

#endif // DICTIONARY_H

// Implements a dictionary's functionality
#include <stdbool.h>
#include <cs50.h>
#include <stdio.h>
#include <math.h>
#include <string.h>
#include <strings.h>
#include <ctype.h>

#include "dictionary.h"

#define HASHSIZE 150000

// define struct for word and node pair
typedef struct node
{
    char word[LENGTH + 1];
    struct node *next;
}
node;

// create hashtable array of lists and set to null at first (also head)
node *hashtable[HASHSIZE] = {NULL};

// this doesn't work, need initializer
//hashtable = NULL;

// word/dict count tracker
int dictCount = 0;

// djb2 hash - http://www.cse.yorku.ca/~oz/hash.html
unsigned long hash(const char *str)
{
    unsigned long hash = 5381;
    int c;

    while ((c = *str++))
    {
        hash = ((hash << 5) + hash) + c;
    }
    return hash;
}

// Returns true if word is in dictionary else false
bool check(const char *word)
{
    char inputWord[LENGTH + 1];
    int wordLen = strlen(word);

    // convert input word to all lowercase for case insensitivty
    for (int i = 0; i < wordLen; i++)
    {
        inputWord[i] = tolower(word[i]);
        // adds null term to end of inputword elements (45 + 1sub)
        inputWord[wordLen] = '\0';
    }

    // generate index from hash
    int indexValue = hash(inputWord) % HASHSIZE;

    // create cursor ptr node to head node in list
    node *cursor = hashtable[indexValue];

    // traversal loop - when cursor has not reached end of list continue
    while (cursor != NULL)
    {
        // compare word entered to dict words, if word found return true
        if (strcasecmp(cursor->word, inputWord) == 0 )
        {
            return true;
        }
        // update cursor value to next node for traversing
        cursor = cursor->next;
    }
    return false;
}

// Loads dictionary into memory, returning true if successful else false
bool load(const char *dictionary)
{
    char word[LENGTH + 1];
    int indexValue;

    FILE *dict = fopen(dictionary, "r");
    if (dict == NULL)
    {
        fprintf(stderr, "File does not exist\n");
        return false;
    }

    // scan dictionary words,
    while (fscanf(dict, "%s", word) != EOF)
    {
        // generate indexes from hash of dictwords
        indexValue = hash(word) % HASHSIZE;

        // creates dynamic temp nodes to store value of dict words
        node *dictwordTemp = malloc(sizeof(node));

        // checks to see if temp node is null, if failed unload memory
        if (dictwordTemp == NULL)
        {
            unload();
            return false;
        }

        // else, put word from temp nodes into word
        strcpy(dictwordTemp->word, word);

        // increment dictionary word count
        dictCount++;

        // check if list at index is empty, if so insert
        if (hashtable[indexValue] == NULL)
        {
            // update the end portion of node to be the last node
            dictwordTemp->next = NULL;
            // update head node link to current value/data node for list
            hashtable[indexValue] = dictwordTemp;
        }

        // if list isn't empty at the index, insert/append elsewhere
        else
        {
            // set node to value of index
            dictwordTemp->next = hashtable[indexValue];
            hashtable[indexValue] = dictwordTemp;
        }
    }
    fclose(dict);
    return true;
}

// Returns number of words in dictionary if loaded else 0 if not yet loaded
unsigned int size(void)
{
    if (dictCount > 0)
    {
        return dictCount;
    }
    else
    {
        return 0;
    }
}

// Unloads dictionary from memory, returning true if successful else false
bool unload(void)
{
    // iterate through all elements in hashtable
    for (int i = 0; i < HASHSIZE; i++)
    {
        // set cursor ptr to beginning/head of hashtable
        node *cursor = hashtable[i];

        // while cursor has not reached end continue loop
        while (cursor != NULL)
        {
            node *temp = cursor;
            // set cursor to continue
            cursor = cursor->next;
            free(temp);
        }
    }
    return true;
}
